const baseUrl="https://api.punkapi.com/v2/beers?page=",checkoutDiv=document.getElementById("checkout-div"),productDiv=document.getElementById("products-div");let page=1,perPage="&per_page=80";const fetchAPI=async()=>{const t=await fetch(baseUrl+page+perPage),e=await t.json();productItems(e),checkoutPrice(e)};fetchAPI();let addToBasket=JSON.parse(localStorage.getItem("data"))||[];const productItems=t=>{if(0!==addToBasket.length)return productDiv.innerHTML=addToBasket.map((e=>{const{id:d,item:a}=e;let n=t.find((t=>t.id===d))||[];const{name:c,abv:o}=n;return`\n          <tr>\n            <td>${c.substring(0,15)}</td>\n            <td>$${o}</td>\n            <td>${a}</td>\n            <td>$${(a*o).toFixed(1)}</td>\n          </tr>\n          `})).join("")},checkoutPrice=t=>{if(0!==addToBasket.length){let e=addToBasket.map((e=>{let d=t.find((t=>t.id===e.id))||[];return e.item*d.abv})).reduce(((t,e)=>t+e),0);checkoutDiv.innerHTML=`\n    <tr>\n     <td colspan="3" class="text-end total-amount">Total:</td>\n     <td class="total-price">$${e.toFixed(1)}</td>\n    </tr>\n       `}};