const gridList=()=>{const e=document.querySelectorAll(".links-view ul li"),l=document.querySelectorAll(".view_wrap"),t=document.getElementsByClassName("list-view"),s=document.getElementsByClassName("grid-view");e.forEach((e=>{e.addEventListener("click",(()=>{let i=e.getAttribute("data-view");if(l.forEach((e=>{e.style.display="none"})),"list-view"===i){for(let e=0;e<t.length;e++)t[e].style.display="block";for(let e of t)e.style.display="block"}else for(let e=0;e<s.length;e++)s[e].style.display="block"}))}))};