const gridList=()=>{const e=document.querySelectorAll(".links-view ul li"),l=document.querySelectorAll(".view_wrap"),t=document.getElementsByClassName("list-view"),i=document.getElementsByClassName("grid-view");e.forEach((e=>{e.addEventListener("click",(()=>{let s=e.getAttribute("data-view");if(l.forEach((e=>{e.style.display="none"})),"list-view"===s)for(let e=0;e<t.length;e++)t[e].style.display="block";else for(let e=0;e<i.length;e++)i[e].style.display="block"}))}))};